import '../../App.css';
import { Link } from 'react-router-dom';
import trondheim from '../../images/trd.jpg'; 
import React, { useState } from 'react';

function Day1() {
  const [userChoice, setUserChoice] = useState('');
  const [result, setResult] = useState('');

  const correctAnswer = '213 000';  // Set the correct answer

  const handleChoice = (choice) => {
    setUserChoice(choice);
    // Check if the user's choice is correct
    if (choice === correctAnswer) {
      setResult(`Det var heilt ${correctAnswer}, riktig, og no er det bare å begynne kryssavhøret. "Imorga reiser vi til Sverige, for eg fant ein svensk kjøttbolle i skoen min" sa Maistortilla-Marius`);
    } else {
      setResult(`Det var dessverre feil, men Ørret-Øystein visste heldigvis betre. Nemleg at antall moglege tjuvrader er ${correctAnswer}. "No er det bare å begynne kryssavhøret. Imorga reiser vi til Sverige, for eg fant ein svensk kjøttbolle i skoen min" sa Maistortilla-Marius'var heilt, riktig, og no er det bare å begynne kryssavhøret. "Imorga reiser vi til Sverige, for eg fant ein svensk kjøttbolle i skoen min" sa Maistortilla-Marius'`);
    }
  };
  return (
    <div className="app">
    <h1 className='title'>Første desember</h1>
      <div className='row'>
        <h3 className='col'>Dato:</h3>
        <h3 className='col'>Den aller første desember, 06:05</h3>
      </div>
      <div className='row'>
        <h3 className='col'>Stad:</h3>
        <h3 className='col'>I ei vakker men kald leiligheit på Nardo</h3>
      </div>
      <h4 className='dinfo'>
        Det er krik sin julefest ikveld, og alle som ein måtte gutane stryke skjorter,
        så når det gjaldt kanelen, måtte gutane våre bare vente. Med tanke på stryk, kom 
        Maistortilla-Marius på eksamenene som kom på løpende bånd, i ei forrykande fart. 
        Guttane var einige om at ein venta med å ta opp jakta til morgandagen, da dei også
        hadde julekalender å sjå. Ka ville Markus Neby og kjendisane hans finne på? 
        Jaja, nok med da, tenkte Ørret-Øystein. Me må finna ut kor monge som kan ha stjåle Kanelen, sa Avokado-Andreas  
      </h4>
      <div className="quiz-container">
      <div className="quiz-image">
        <img src={trondheim} alt="trondheim" className='quiz-image' />
        <br></br>
      </div>
      <div className="quiz-content">
        <h2>Angående antal moglege tjuvradar:</h2>
        <p>kor mangen bor i Trondheim? Velg ein av alternativa under</p>
        <h2>Alternativer:</h2>
        <ul>
          <li onClick={() => handleChoice('211 000')}>211 000</li>
          <li onClick={() => handleChoice('212 000')}>212 000</li>
          <li onClick={() => handleChoice('213 000')}>213 000</li>
        </ul>
        <br></br>
        {result && <h3>{result}</h3>}
      </div>
    </div>
    <Link to="/xmas_site/" className='back-button' >Tilbage te heimesio</Link>
    </div>
  );
}

export default Day1;
