import '../../App.css';
import { Link } from 'react-router-dom';
import julekort from '../../images/Friggs.jpg'; 
import React, { useState } from 'react';

function Day2() {
  const [userChoice, setUserChoice] = useState('');
  const [result, setResult] = useState('');

  const correctAnswer = '89';  // Set the correct answer

  const handleChoice = (choice) => {
    setUserChoice(choice);
    // Check if the user's choice is correct
    if (choice === correctAnswer) {
      setResult(`Svaret var riktig, 3/4 fysmattere bor oppe og 4/5 kybbere (+ 1 bygg) nede ifølge Ørret-Øystein`);
    } else {
      setResult(`Det var dessverre feil, vi er ${correctAnswer}% sivinger. 3/4 fysmattere bor oppe og 4/5 kybbere (+ 1 bygg) nede ifølge Ørret-Øystein`);
    }
  };
  return (
    <div className="app">
    <h1 className='title'>Andre desember</h1>
      <div className='row'>
        <h3 className='col'>Dato:</h3>
        <h3 className='col'>Den andre desember, 13:55</h3>
      </div>
      <div className='row'>
        <h3 className='col'>Stad:</h3>
        <h3 className='col'>I enda ei kald leiligheit på Nardo (Friggs veg )</h3>
      </div>


      <div className="quiz-container">

      <div className="quiz-content">
      <h4 className='dinfo'>
      <div className="quiz-image">
      <img src={julekort} alt="julekort" className='quiz-image' />
        <br></br>
      </div>
        Gutane har knapt fått sove. Den vanlege havregrauten liknar ikkje seg sjølv, utan det gyldne kanelet. 
        Sjølv om dei er konfliktsky, må dei konfrontere dei staute damene som bor lenger nede på Nardo. Turen er kort, men huset er fullt av grønne planter.
        Hot-Kyllingsuppe-Hannah står og vanner planter, mens ho kjem med ein biologifakta om opphavet til navnet til ein blom. Julewok-Johanna øver på Pull-ups i stanga i kjøkkeninngangen. Hylleblomstsaft-Hanna varmer Tè til gjestene, medan Drue-Dorte fortel om joggeturen ho var på imorges.
        Etter eit koseleg kvarter snur stemninga seg på hovudet. Anklager dere oss? -Raser det fra    julefest ikveld, og gutane måtte stryke skjorter, alle som ein.
        Ettersom tida var knapp, gjekk dei kanelsultne torsgutta opp til dei nærmaste naboane, nemleg Tornardos.
        Da Avokado-Andreas åpna døra vart han møtt av ein syngande Nellik-Nikolai, ein dansande Muskat-Mathias, ein humrande Honning-Henning og 
        ein alvorlig Muscovadosukker-Markus. Har dykk sett nokon mogleg tjuvra
      </h4>
        <h4>
          Eg såg ein mørk skygge i hu og hast mot Friggs veg, kom det kontant frå Muscovadosukker-Markus. Gutane var sjokkerte, men måtte tilbake til skjortestrykinga. 
          Håper at det er den einaste strykinga dei treng å gjere i år. 
        </h4><br></br>
        <h2>Apropos Tors veg 12:</h2>
        <h2>kor høg andel sivingstudenter bor det i huset vårt? Velg ein av alternativa under, og klikk på den du trur er riktig</h2>
        <h2>Alternativer:</h2>
        <ul>
          <li onClick={() => handleChoice('89')}>89%</li>
          <li onClick={() => handleChoice('22')}>22%</li>
          <li onClick={() => handleChoice('56')}>56%</li>
        </ul>
        <br></br>
        {result && <h3>{result}</h3>}
      </div>
    </div>
    <Link to="/xmas_site/" className='back-button' >Tilbage te heimesio</Link>
    </div>
  );
}

export default Day2;
