import React, { useState, useEffect } from 'react';
import img1 from '../../images/1.jpg'; 
import img2 from '../../images/2.jpg'; 


const Slideshow = () => {
  const [currentImage, setCurrentImage] = useState(1);

  useEffect(() => {
    // Set up a timer to change the image every 3 seconds
    const intervalId = setInterval(() => {
      // Increment the current image number (looping back to 1 after 12)
      setCurrentImage((prevImage) => (prevImage % 12) + 1);
    }, 2000);

    // Clear the interval when the component is unmounted or updated
    return () => clearInterval(intervalId);
  }, [currentImage]);

  return (
    <div className="slideshow">
      <img
        src={`../../images/${currentImage}.jpg`}
        alt={`Slide ${currentImage}`}
        style={{ maxWidth: '100%', maxHeight: '100%' }}
      />
    </div>
  );
};

export default Slideshow;
