import React from 'react';
import './App.css';
import lottie from 'lottie-web';
import xmasAnim from './xmas_anim.json';

class App extends React.Component {
  render() {
    const daysUntilChristmas = 24;
    const calendar = [];

    for (let day = 1; day <= daysUntilChristmas; day++) {
      calendar.push(
        <div key={day} className="calendar-square">
          {day}
        </div>
      );
    }

    return (
      <div className="app">
        <h1>Torsguttas gledelige jul</h1>
        <div className="calendar-container">{calendar}</div>
        <div
          className="xmas-anim"
          ref={(ref) => (this.animationContainer = ref)}
        ></div>
        <div
          className="cat-anim"
          ref={(ref) => (this.animationContainer = ref)}
        ></div>
      </div>
    );
  }

  constructor(props) {
    super(props);
    this.animationLoaded = false; // Track whether animation is loaded
  }

  componentDidMount() {
    if (!this.animationLoaded) {
      lottie.loadAnimation({
        container: this.animationContainer,
        renderer: 'svg',
        loop: true,
        autoplay: true,
        animationData: xmasAnim,
      });
      this.animationLoaded = true; // Mark animation as loaded
    }
  }  
}

export default App;
